const { core } = require('./core')

module.exports = {
  get_open_types: async () => {
    const response = await core.query('PK = :p_key AND SK = :s_key', {
      ':p_key': '#METADATA#CONST',
      ':s_key': '#OPEN_TYPES',
    })
    return (response.Items && response.Items.length > 0) ? response.Items[0]['OpenTypes'] : {}
  },
}
