exports.response_ok = (res_data) => {
  return {
    statusCode: 200,
    headers: {
      'x-custom-header': 'my custom header value',
      'Access-Control-Allow-Origin': '*'
    },
    body: JSON.stringify({
      data: res_data,
    })
  };
}

exports.response_400 = {
  statusCode: 400,
  headers: {
    'x-custom-header': 'my custom header value',
    "Access-Control-Allow-Origin": "*",
    "Access-Control-Allow-Headers": "Origin, X-Requested-With, Content-Type, Authorization, Accept",
  }
}

exports.response_403 = {
  statusCode: 403,
}